import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';

const MyCourses = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useAuth();

  useEffect(() => {
    const fetchMyCourses = async () => {
      try {
        const token = localStorage.getItem('token');
        console.log('ðŸ” Fetching my courses for user:', user?.username);
        
        const response = await axios.get('http://localhost:8000/api/courses/my_courses/', {
          headers: {
            'Authorization': `Token ${token}`
          }
        });
        
        console.log('âœ… My courses API response:', response.data);
        setCourses(response.data);
        setError('');
      } catch (error) {
        console.error('âŒ Error fetching my courses:', error);
        console.error('âŒ Error response:', error.response?.data);
        setError('Failed to load your courses. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchMyCourses();
    } else {
      setLoading(false);
    }
  }, [user]);

  const getThumbnailUrl = (thumbnailPath) => {
    if (!thumbnailPath) return null;
    if (thumbnailPath.startsWith('http')) return thumbnailPath;
    return `http://localhost:8000${thumbnailPath}`;
  };

  if (loading) {
    return (
      <div className="container">
        <h1>My {user?.user_type === 'instructor' ? 'Teaching' : 'Learning'}</h1>
        <p>Loading your courses...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container">
        <h1>My {user?.user_type === 'instructor' ? 'Teaching' : 'Learning'}</h1>
        <div className="error-message" style={{ color: 'red', padding: '1rem', background: '#ffe6e6', borderRadius: '4px' }}>
          {error}
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <h1>My {user?.user_type === 'instructor' ? 'Teaching' : 'Learning'}</h1>
      
      {courses.length === 0 ? (
        <div className="card">
          <h3>No courses yet</h3>
          {user?.user_type === 'instructor' ? (
            <p>You haven't created any courses yet. <Link to="/courses/create">Create your first course</Link>.</p>
          ) : (
            <div>
              <p>You haven't enrolled in any courses yet.</p>
              <p><Link to="/courses" className="btn btn-primary">Browse Available Courses</Link></p>
            </div>
          )}
        </div>
      ) : (
        <div>
          <p>Found {courses.length} course{courses.length !== 1 ? 's' : ''}</p>
          <div className="courses-grid">
            {courses.map(course => (
              <div key={course.id} className="course-card">
                {course.thumbnail && (
                  <img 
                    src={getThumbnailUrl(course.thumbnail)} 
                    alt={course.title}
                    className="course-thumbnail"
                    onError={(e) => {
                      e.target.style.display = 'none';
                    }}
                  />
                )}
                <h3>{course.title}</h3>
                <p>{course.description}</p>
                <p><strong>Instructor:</strong> {course.instructor_name}</p>
                <p><strong>Level:</strong> {course.level}</p>
                <p><strong>Duration:</strong> {course.duration_hours} hours</p>
                
                {user?.user_type === 'instructor' ? (
                  <div style={{ marginTop: '10px' }}>
                    <button className="btn btn-secondary" style={{ marginRight: '10px' }}>
                      Manage Course
                    </button>
                    <button className="btn btn-primary">
                      View Analytics
                    </button>
                  </div>
                ) : (
                  <Link to={`/learn/${course.id}`} className="btn btn-primary" style={{ marginTop: '10px', display: 'block' }}>
                    Continue Learning
                  </Link>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default MyCourses;
