import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';

const MyCourses = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useAuth();

  useEffect(() => {
    const fetchMyCourses = async () => {
      try {
        const token = localStorage.getItem('token');
        console.log('ðŸ” Fetching my courses for user:', user?.username);
        
        // Use the correct endpoint for enrolled courses
        const response = await axios.get('http://localhost:8000/api/courses/my_courses/', {
          headers: {
            'Authorization': `Token ${token}`
          }
        });
        
        console.log('âœ… My courses API response:', response.data);
        setCourses(response.data);
        setError('');
      } catch (error) {
        console.error('âŒ Error fetching my courses:', error);
        console.error('âŒ Error details:', error.response?.data);
        setError('Failed to load your courses. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchMyCourses();
    } else {
      setLoading(false);
    }
  }, [user]);

  // Function to get thumbnail URL
  const getThumbnailUrl = (thumbnailPath) => {
    if (!thumbnailPath) return null;
    if (thumbnailPath.startsWith('http')) return thumbnailPath;
    return `http://localhost:8000${thumbnailPath}`;
  };

  if (loading) {
    return (
      <div className="container">
        <h1>My Learning</h1>
        <p>Loading your courses...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container">
        <h1>My Learning</h1>
        <div className="error-message">
          {error}
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <h1>My Learning</h1>
      <p>Your enrolled courses</p>
      
      {courses.length === 0 ? (
        <div className="card">
          <h3>No courses enrolled yet</h3>
          <p>You haven't enrolled in any courses yet. <Link to="/courses">Browse available courses</Link> to get started with your learning journey.</p>
          <Link to="/courses" className="btn btn-primary">
            Browse Courses
          </Link>
        </div>
      ) : (
        <div>
          <p>You are enrolled in {courses.length} course{courses.length !== 1 ? 's' : ''}</p>
          <div className="courses-grid">
            {courses.map(course => (
              <div key={course.id} className="course-card">
                {course.thumbnail && (
                  <img 
                    src={getThumbnailUrl(course.thumbnail)} 
                    alt={course.title}
                    className="course-thumbnail"
                    onError={(e) => {
                      e.target.style.display = 'none';
                    }}
                  />
                )}
                <h3>{course.title}</h3>
                <p>{course.description}</p>
                <p><strong>Instructor:</strong> {course.instructor_name}</p>
                <p><strong>Level:</strong> {course.level}</p>
                <p><strong>Duration:</strong> {course.duration_hours} hours</p>
                
                <Link to={`/learn/${course.id}`} className="btn btn-primary" style={{ marginTop: '10px', display: 'block' }}>
                  Continue Learning
                </Link>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default MyCourses;
