import React from 'react';
import { useAuth } from '../contexts/AuthContext';

const MyCourses = () => {
  const { user } = useAuth();

  return (
    <div className="container">
      <h1>My {user?.user_type === 'instructor' ? 'Teaching' : 'Learning'}</h1>
      <div className="card">
        <p>This page will show {user?.user_type === 'instructor' ? 'courses you are teaching' : 'courses you are enrolled in'}.</p>
        <p>Functionality coming soon!</p>
      </div>
    </div>
  );
};

export default MyCourses;
