import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Dashboard = () => {
  const { user, logout } = useAuth();

  if (!user) {
    return (
      <div className="container">
        <h1>Dashboard</h1>
        <p>Please log in to view your dashboard.</p>
      </div>
    );
  }

  return (
    <div className="container">
      <h1>Welcome, {user.first_name || user.username}!</h1>
      <p><strong>Role:</strong> {user.user_type}</p>
      
      {user.user_type === 'instructor' ? (
        <InstructorDashboard user={user} />
      ) : (
        <StudentDashboard user={user} />
      )}
      
      <button onClick={logout} className="btn btn-primary" style={{ marginTop: '15px' }}>
        Logout
      </button>
    </div>
  );
};

// Instructor-specific dashboard
const InstructorDashboard = ({ user }) => {
  return (
    <div>
      <div className="card">
        <h3>Instructor Dashboard</h3>
        <p>Manage your courses and track student progress.</p>
        
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px', marginTop: '20px' }}>
          <div className="course-card">
            <h4>Create New Course</h4>
            <p>Design and publish a new course for students.</p>
            <Link to="/courses/create" className="btn btn-primary">
              Create Course
            </Link>
          </div>
          
          <div className="course-card">
            <h4>My Courses</h4>
            <p>View and manage your existing courses.</p>
            <Link to="/courses/my-courses" className="btn btn-primary">
              View My Courses
            </Link>
          </div>
          
          <div className="course-card">
            <h4>Student Progress</h4>
            <p>Monitor how students are progressing in your courses.</p>
            <Link to="/analytics" className="btn btn-primary">
              View Analytics
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

// Student-specific dashboard - UPDATED TO SHOW ACTUAL COURSES
const StudentDashboard = ({ user }) => {
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch enrolled courses when component loads
  useEffect(() => {
    const fetchEnrolledCourses = async () => {
      try {
        const token = localStorage.getItem('token');
        console.log('ðŸ“Š Dashboard: Fetching enrolled courses...');
        
        const response = await axios.get('http://localhost:8000/api/courses/my_courses/', {
          headers: {
            'Authorization': `Token ${token}`
          }
        });
        
        console.log('âœ… Dashboard courses loaded:', response.data);
        setEnrolledCourses(response.data);
      } catch (error) {
        console.error('âŒ Dashboard: Error fetching courses:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchEnrolledCourses();
  }, []);

  const getThumbnailUrl = (thumbnailPath) => {
    if (!thumbnailPath) return null;
    if (thumbnailPath.startsWith('http')) return thumbnailPath;
    return `http://localhost:8000${thumbnailPath}`;
  };

  return (
    <div>
      {/* Enrolled Courses Section - SHOWS ACTUAL COURSES */}
      <div className="card">
        <h3>My Enrolled Courses</h3>
        
        {loading ? (
          <p>Loading your courses...</p>
        ) : enrolledCourses.length === 0 ? (
          <div>
            <p>You haven't enrolled in any courses yet.</p>
            <Link to="/courses" className="btn btn-primary">
              Browse Courses to Get Started
            </Link>
          </div>
        ) : (
          <div>
            <p>You are enrolled in {enrolledCourses.length} course{enrolledCourses.length !== 1 ? 's' : ''}</p>
            
            {/* Display enrolled courses in a grid */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '20px', marginTop: '20px' }}>
              {enrolledCourses.map(course => (
                <div key={course.id} className="course-card" style={{ 
                  border: '1px solid #ddd', 
                  borderRadius: '8px', 
                  padding: '16px',
                  background: 'white'
                }}>
                  {course.thumbnail && (
                    <img 
                      src={getThumbnailUrl(course.thumbnail)} 
                      alt={course.title}
                      style={{ 
                        width: '100%', 
                        height: '160px', 
                        objectFit: 'cover', 
                        borderRadius: '4px', 
                        marginBottom: '12px' 
                      }}
                      onError={(e) => {
                        e.target.style.display = 'none';
                      }}
                    />
                  )}
                  <h4 style={{ margin: '0 0 8px 0' }}>{course.title}</h4>
                  <p style={{ margin: '0 0 8px 0', color: '#666', fontSize: '14px' }}>
                    {course.description?.substring(0, 80)}...
                  </p>
                  <p style={{ margin: '4px 0', fontSize: '14px' }}>
                    <strong>Instructor:</strong> {course.instructor_name}
                  </p>
                  <p style={{ margin: '4px 0', fontSize: '14px' }}>
                    <strong>Level:</strong> {course.level}
                  </p>
                  
                  <Link 
                    to={`/learn/${course.id}`} 
                    className="btn btn-primary"
                    style={{ 
                      display: 'block', 
                      marginTop: '12px', 
                      textAlign: 'center',
                      padding: '8px 12px'
                    }}
                  >
                    Continue Learning
                  </Link>
                </div>
              ))}
            </div>
            
            {/* View All Courses Button */}
            <div style={{ textAlign: 'center', marginTop: '20px' }}>
              <Link to="/courses/my-courses" className="btn btn-secondary">
                View All My Courses
              </Link>
            </div>
          </div>
        )}
      </div>

      {/* Quick Actions Section */}
      <div className="card" style={{ marginTop: '20px' }}>
        <h3>Quick Actions</h3>
        <p>Explore more learning opportunities and manage your account.</p>
        
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '15px', marginTop: '15px' }}>
          <div style={{ textAlign: 'center' }}>
            <Link to="/courses" className="btn btn-primary" style={{ display: 'block' }}>
