import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';

const Courses = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/courses/');
        setCourses(response.data.results || response.data);
      } catch (error) {
        console.error('Error fetching courses:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, []);

 const handleEnroll = async (courseId) => {
  if (!user) {
    alert('Please log in to enroll in courses');
    return;
  }

  if (user.user_type !== 'student') {
    alert('Only students can enroll in courses');
    return;
  }

  try {
    const token = localStorage.getItem('token');
    await axios.post(`http://localhost:8000/api/courses/${courseId}/enroll/`, {}, {
      headers: {
        'Authorization': `Token ${token}`
      }
    });
    
    alert('Successfully enrolled in the course!');
    // Optionally refresh the page or update state
  } catch (error) {
    console.error('Enrollment error:', error);
    alert(error.response?.data?.error || 'Enrollment failed');
  }
};

  if (loading) {
    return (
      <div className="container">
        <h1>Courses</h1>
        <p>Loading courses...</p>
      </div>
    );
  }

  return (
    <div className="container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h1>Available Courses</h1>
        {user?.user_type === 'instructor' && (
          <Link to="/courses/create" className="btn btn-primary">
            Create New Course
          </Link>
        )}
      </div>

      <div className="courses-grid">
        {courses.length > 0 ? (
          courses.map(course => (
            <div key={course.id} className="course-card">
              <h3>{course.title}</h3>
              <p>{course.description}</p>
              <p><strong>Instructor:</strong> {course.instructor_name}</p>
              <p><strong>Level:</strong> {course.level}</p>
              <p><strong>Duration:</strong> {course.duration_hours} hours</p>
              
              {user?.user_type === 'student' ? (
                <button 
                  onClick={() => handleEnroll(course.id)} 
                  className="btn btn-primary" 
                  style={{ marginTop: '10px' }}
                >
                  Enroll Now
                </button>
              ) : user?.user_type === 'instructor' ? (
                <div style={{ marginTop: '10px' }}>
                  <button className="btn btn-secondary" style={{ marginRight: '10px' }}>
                    Edit Course
                  </button>
                  <button className="btn btn-primary">
                    View Analytics
                  </button>
                </div>
              ) : (
                <Link to="/login" className="btn btn-primary" style={{ marginTop: '10px', display: 'block' }}>
                  Login to Enroll
                </Link>
              )}
            </div>
          ))
        ) : (
          <div className="card">
            <p>No courses available yet.</p>
            {user?.user_type === 'instructor' && (
              <Link to="/courses/create" className="btn btn-primary">
                Create the First Course
              </Link>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Courses;
