import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';

const MyCourses = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  useEffect(() => {
    const fetchMyCourses = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:8000/api/courses/my_courses/', {
          headers: {
            'Authorization': `Token ${token}`
          }
        });
        setCourses(response.data);
      } catch (error) {
        console.error('Error fetching my courses:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchMyCourses();
    }
  }, [user]);

  if (loading) {
    return (
      <div className="container">
        <h1>My {user?.user_type === 'instructor' ? 'Teaching' : 'Learning'}</h1>
        <p>Loading your courses...</p>
      </div>
    );
  }

  return (
    <div className="container">
      <h1>My {user?.user_type === 'instructor' ? 'Teaching' : 'Learning'}</h1>
      
      {courses.length === 0 ? (
        <div className="card">
          <h3>No courses yet</h3>
          {user?.user_type === 'instructor' ? (
            <p>You haven't created any courses yet. <Link to="/courses/create">Create your first course</Link>.</p>
          ) : (
            <p>You haven't enrolled in any courses yet. <Link to="/courses">Browse available courses</Link>.</p>
          )}
        </div>
      ) : (
        <div className="courses-grid">
          {courses.map(course => (
            <div key={course.id} className="course-card">
              {course.thumbnail && (
                <img 
                  src={`http://localhost:8000${course.thumbnail}`} 
                  alt={course.title}
                  className="course-thumbnail"
                  onError={(e) => {
                    e.target.style.display = 'none';
                  }}
                />
              )}
              <h3>{course.title}</h3>
              <p>{course.description}</p>
              <p><strong>Instructor:</strong> {course.instructor_name}</p>
              <p><strong>Level:</strong> {course.level}</p>
              <p><strong>Duration:</strong> {course.duration_hours} hours</p>
              
              {user?.user_type === 'instructor' ? (
                <div style={{ marginTop: '10px' }}>
                  <button className="btn btn-secondary" style={{ marginRight: '10px' }}>
                    Manage Course
                  </button>
                  <button className="btn btn-primary">
                    View Analytics
                  </button>
                </div>
              ) : (
                <Link to={`/learn/${course.id}`} className="btn btn-primary" style={{ marginTop: '10px', display: 'block' }}>
                  Continue Learning
                </Link>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MyCourses;
